import React from 'react';
import './App.css';
import Navbar from './components/Navbar.js';
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';

const Employee = props => (
  <tr>
  <td>{props.employees.EmployeeID}</td>
  <td>{props.employees.EmployeeName}</td>
  <td>
    <div class="custom-control custom-checkbox">
      <input type="checkbox" class="custom-control-input" id="customCheck1" checked={ prop.employees.EmployeeAvailability==true ? "checked" : "unchecked" } />
      <label class="custom-control-label" for="customCheck1"></label>
    </div>
  </td>
  <td>
    <button type="button" class="btn btn-outline-info btn-sm" data-toggle="modal" data-target="#addEmployeeModal">
      <i class="fa fa-edit"></i>&nbsp; Edit
    </button>
    <button type="button" class="btn btn-outline-danger btn-sm">
      <i class="fa fa-trash"></i>&nbsp; Delete
    </button>
  </td>
</tr>
)

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {employees: []};
  }

  componentDidMount() {
    axios.get('http://localhost:4000/employee/')
      .then(response => {
        this.setState({ employees: response.data });
      })
      .catch(function (error){
        console.log(error);
      })
  }

  todoList() {
    return this.state.todos.map(function(currentTodo, i){
      return <Todo todo={currentTodo} key={i} />;
    })
  }
  render() {
    return(
      <div>
        <Navbar />
        <div class="container-fluid">
          <div class="row">
            <div class="col-12">
              <div class="question-dashboard">
                <div class="card mt-4 mb-3 mb-md-4">
                  <div class="card-body p-3">
                    <h5 class="text-secondary mb-2">Available: <span
                        class="font-weight-bold ml-1 text-dark">08</span></h5>
                    <h5 class="text-secondary">Total: <span class="font-weight-bold ml-1 text-dark">50</span>
                    </h5>
                    ​
                    <button class="btn btn-primary mt-4" data-toggle="modal" data-target="#addEmployeeModal">
                      <i class="fa fa-plus"></i>&nbsp; Add Employee</button>
                  </div>
                </div>
                ​
                <div class="table-responsive mt-3 mt-md-4 mb-2">
                  <table class="table table-bordered">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Department</th>
                        <th>Available</th>
                        <th>View Details</th>
                      </tr>
                    </thead>
                    <tbody>

                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App;
