import React from 'react';
import './App.css';
import Navbar from './components/Navbar.js';
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';

const Employee = props => (
  <tr>
  <td>{props.employee.EmployeeID}</td>
  <td>{props.employee.EmployeeName}</td>
  <td>
    <div className="custom-control custom-checkbox">
      <input type="checkbox" className="custom-control-input" id="customCheck1" checked={ props.employee.EmployeeAvailability===true ? "checked" : "unchecked" } />
      <label className="custom-control-label" for="customCheck1"></label>
    </div>
  </td>
  <td>{props.employee.EmployeeAge}</td>
  <td>
    <button type="button" className="btn btn-outline-info btn-sm" data-toggle="modal" data-target="#addEmployeeModal">
      <i className="fa fa-edit"></i>&nbsp; Edit
    </button>
    <button type="button" className="btn btn-outline-danger btn-sm">
      <i className="fa fa-trash"></i>&nbsp; Delete
    </button>
  </td>
</tr>
)

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      employees: []
    };
  }

  componentDidMount() {
    axios.get('http://localhost:4000/employee/')
      .then(response => {
        this.setState({  employees: response.data });

      })
      .catch(function (error){
        console.log(error);
      })
  }

  EmployeeList() {
    console.log(this.state.employees);
    return this.state.employees.map(function(currentEmployee, i){
      return <Employee employee={currentEmployee} key={i} />;
    })
  }
  render() {
    return(
      <div>
        <Navbar />
        <div className="container-fluid">
          <div className="row">
            <div className="col-12">
              <div className="question-dashboard">
                <div className="card mt-4 mb-3 mb-md-4">
                  <div className="card-body p-3">
                    <h5 className="text-secondary mb-2">Available: <span
                        className="font-weight-bold ml-1 text-dark">08</span></h5>
                    <h5 className="text-secondary">Total: <span className="font-weight-bold ml-1 text-dark">50</span>
                    </h5>
                    ​
                    <button className="btn btn-primary mt-4" data-toggle="modal" data-target="#addEmployeeModal">
                      <i className="fa fa-plus"></i>&nbsp; Add Employee</button>
                  </div>
                </div>
                ​
                <div className="table-responsive mt-3 mt-md-4 mb-2">
                  <table className="table table-bordered">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Department</th>
                        <th>Available</th>
                        <th>Age</th>
                        <th>View Details</th>
                      </tr>
                    </thead>
                    <tbody>
                      {this.EmployeeList }
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App;
